import { Component , OnInit } from '@angular/core';
import { HttpClient, HttpParams  } from '@angular/common/http';
import { map } from 'rxjs/operators';
import { LocalStorageService } from '../../services/local-storage-service.service';
import { ActivatedRoute } from '@angular/router';
import { ProductsService } from 'src/app/services/products.service';


interface Card {
  id: number;
  name: string;
  image: string;
  // Add other properties as needed
}
@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {
  products: any[] = [];
  private cardId!: number; 
  constructor(private route: ActivatedRoute, private dataService: ProductsService, private http: HttpClient, private localStorageService: LocalStorageService) {}


  onCardClick(card: any) {
    this.localStorageService.addCard(card);
  }

  onClearLocalStorageClick() {
    this.localStorageService.clearStoredCards();
  }



  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.cardId = +params['id']; // Use the + operator to convert the string to an integer
      // console.log( this.cardId); // This will log the card.id value from the clicked card
    });
    this.fetchproducts();
  }

  fetchproducts(): void {
    console.log('the clicked category id is : ', this.cardId); // Now you can access the cardId here


    this.dataService.getProducts(this.cardId).subscribe(
      (data) => {
        this.products = data;
      },
      (error) => {
        console.error('Error fetching data:', error);
      }
    );

